package javaLoops1;

public class javaLoops1 {
	
	public static void Solution(int N) {
		for (int i = 1; i < 11; i++) {
			System.out.println(N + " x " + i + " = " + N*i);
		}
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Sample Input 0:
		int N = 2;
		javaLoops1.Solution(N);
	}

}
